import { Component, OnInit } from '@angular/core';

import { Message } from '../message.model';

@Component({
  selector: 'cms-message-list',
  templateUrl: './message-list.component.html',
  styleUrls: ['./message-list.component.css'],
})
export class MessageListComponent implements OnInit {
  messages: Message[] = [
    new Message('1', 'Subject 1', 'Message Text 1', 'Brother Wilson'),
    new Message('2', 'Subject 1', 'Message Text 2', 'Brother Wilson'),
    new Message('3', 'Subject 1', 'Message Text 3', 'Brother Wilson'),
  ];

  constructor() {}

  ngOnInit() {}

  onAddMessage(message: Message) {
    this.messages.push(message);
  }
}
<div class="row">
  <div class="col-md-5">
    <div class="panel panel-default">
      <div class="panel-heading">
        <div class="row pad-left-right">
          <span class="title pull-left">Messages</span>
        </div>
      </div>
      <div class="panel-body">
        <div class="row">
          <div class="col-xs-12">
            <!--Add tag to load the MessageItemComponent-->
            <!-- inputMessage comes from message-item.component.ts -->
            <cms-message-item
              *ngFor="let messageEl of messages"
              [inputMessage]="messageEl"></cms-message-item>
          </div>
        </div>
      </div>
      <div class="panel-footer">
        <!--Add tag to load the MessageEditComponent-->
        <cms-message-edit
        (addMessageEvent)="onAddMessage($event)"></cms-message-edit>
      </div>
    </div>
  </div>
</div>
