import { Component, OnInit, Input } from '@angular/core';

import { Message } from '../message.model';
import { Contact } from 'src/app/contacts/contact.model';
import { ContactService } from 'src/app/contacts/contact.service';

@Component({
  selector: 'cms-message-item',
  templateUrl: './message-item.component.html',
  styleUrls: ['./message-item.component.css'],
})
export class MessageItemComponent implements OnInit {
  // inputMessage can be message. but it needs to be in the following locations
  // -- message-item.component.html
  // -- message-list.component.html
  @Input() inputMessage: Message;
  messageSender: string;

  constructor(private contactService: ContactService) {}

  ngOnInit() {
    const contact: Contact = this.contactService.getContact(this.inputMessage.sender);
    this.messageSender = contact.name;
  }
}
<a class="list-group-item clearfix">
  <div>
    <!-- inputMessage comes from message-item.component.ts -->
    <span class="messageHeader">{{ messageSender }}</span>
    <div class="messageText">{{ inputMessage?.msgText }}</div>
  </div>
</a>
