import { Component, Input } from '@angular/core';
import { ActivatedRoute, Params, Router } from '@angular/router';
import { Contact } from '../contact.model';
import { contactService } from '../contact.service';

@Component({
  selector: 'app-contact-detail',
  templateUrl: './contact-detail.component.html',
  styleUrls: ['./contact-detail.component.css']
})
export class ContactDetailComponent {
  @Input() contact: Contact;
  detailContact: Contact[];
  id: string;

  constructor(private contactService: contactService, private route: ActivatedRoute, private router: Router) {

  }

  ngOnInit() {
    this.route.params.subscribe(
      (params: Params) => {
        this.id = params["id"];
        this.contact = this.contactService.getContact(this.id);
      }
    );
  }

  onEditContact() {
    this.router.navigate(["edit"], {relativeTo: this.route});
  }

  onDelete() {
    this.contactService.deleteContact(this.contact);
    // this.router.navigate(["contacts"], {relativeTo: this.route});
    // this.router.navigateByUrl("contacts", {relativeTo: this.route});
    this.router.navigateByUrl('contacts');
  }

}
<div class="row">
    <div class="col-xs-12">
        <div class="panel panel-default">
            <div class="panel-heading custom-flex">
                <div class="row">
                    <div class="col-xs-12">
                        <h2>{{ contact.name }}</h2>
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-6">
                        <span class="pull-left">
                            <img src="{{ contact.imageUrl }}" alt="{{ contact.name }}" class="img-responsive" style="max-height: 50px;">
                        </span>
                    </div>
                    <div class="col-xs-6">
                        <a (click)="onEditContact()" class="btn btn-primary">Edit</a>
                        <a (click)="onDelete()" class="btn btn-danger">Delete</a>
                    </div>
                </div>
            </div>
            <div class="panel-body">
                <div class="row">
                    <div class="col-xs-12">
                        <p>Email: {{ contact.email }}</p>
                        <p>Phone: {{ contact.phone }}</p>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>
