import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { ActivatedRoute, Params, Router } from '@angular/router';

import { Contact } from '../contact.model';
import { ContactService } from '../contact.service';

@Component({
  selector: 'cms-contact-edit',
  templateUrl: './contact-edit.component.html',
  styleUrls: ['./contact-edit.component.css'],
})
export class ContactEditComponent implements OnInit {
  originalContact: Contact;
  contact: Contact;
  groupContacts: Contact[] = [];
  editMode: boolean = false;
  id: string;
  invalidContact = false;

  constructor(
    private contactService: ContactService,
    private router: Router,
    private route: ActivatedRoute
  ) {}

  ngOnInit() {
    this.route.params.subscribe((params: Params) => {
      this.id = params['id'];

      if (!this.id) {
        this.editMode = false;
        return;
      }

      this.originalContact = this.contactService.getContact(this.id);

      if (!this.originalContact) {
        return;
      }

      this.editMode = true;

      this.contact = JSON.parse(JSON.stringify(this.originalContact));

      if (this.originalContact.group && this.originalContact.group.length > 0) {
        this.groupContacts = JSON.parse(
          JSON.stringify(this.originalContact.group)
        );
      }
    });
  }

  onCancel() {
    this.router.navigate(['../'], { relativeTo: this.route });
    // this.router.navigate(['/contacts']);
  }

  isInvalidContact(newContact: Contact) {
    if (!newContact) {
      console.log('New Contact');
      return (this.invalidContact = true);
    }

    if (this.contact && newContact.id === this.contact.id) {
      console.log('Contact already exsists.');
      return (this.invalidContact = true);
    }

    for (let i = 0; i < this.groupContacts.length; i++) {
      console.log('Whatever!');
      // this.invalidContact = false;
      if (newContact.id === this.groupContacts[i].id) {
      return (this.invalidContact = true);
      }
    }
    console.log('Returned false');
      return (this.invalidContact = false);
  }

  addToGroup($event: any) {
    // console.log('dragging contact');
    const selectedContact: Contact = $event.dragData;
    const invalidGroupContact = this.isInvalidContact(selectedContact);
    if (invalidGroupContact) {
      return;
    }
    this.groupContacts.push(selectedContact);
  }

  onRemoveItem(index: number) {
    if (index < 0 || index >= this.groupContacts.length) {
      return;
    }

    this.groupContacts.splice(index, 1);
    this.invalidContact = false;
  }

  onSubmit(form: NgForm) {
    const value = form.value;
    const newContact = new Contact(
      '',
      value.name,
      value.email,
      value.phone,
      value.imageUrl,
      this.groupContacts
    );

    if (this.editMode) {
      this.contactService.updateContact(this.originalContact, newContact);
    } else {
      this.contactService.addContact(newContact);
    }

    this.router.navigate(['/contacts']);
  }
}
<div class="panel panel-default">
  <div class="panel-body">
    <form id="contact-edit" #f="ngForm" (ngSubmit)="onSubmit(f)">
      <div class="row">
        <div class="col-sm-12 form-group">
          <label for="name">Name:</label>
          <input
            type="text"
            id="name"
            class="form-control required"
            size="120"
            max="120"
            name="name"
            [ngModel]="contact?.name"
            #name="ngModel"
            required
          />
        </div>
      </div>

      <div class="row">
        <div class="col-sm-12 form-group">
          <label for="email">Email:</label>
          <input
            type="text"
            id="email"
            class="form-control required"
            size="120"
            max="255"
            email
            name="email"
            [ngModel]="contact?.email"
            #email="ngModel"
            pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,3}$"
            required
          />
        </div>
      </div>

      <div class="alert alert-danger" *ngIf="!email.valid && email.touched">
        Enter a valid Email address
      </div>

      <div class="row">
        <div class="col-sm-12 form-group">
          <label for="phone">Phone:</label>
          <input
            type="text"
            id="phone"
            class="form-control invalid"
            size="150"
            max="255"
            name="phone"
            [ngModel]="contact?.phone"
            #phone="ngModel"
            pattern="\D*([2-9]\d{2})(\D*)([2-9]\d{2})(\D*)(\d{4})\D*"
          />
        </div>
      </div>

      <div class="alert alert-danger" *ngIf="!phone.valid">
        Enter a valid phone number
      </div>

      <div class="row">
        <div class="col-sm-12 form-group">
          <label for="imageUrl">Image URL:</label>
          <input
            type="text"
            id="imageUrl"
            class="form-control"
            size="150"
            max="255"
            name="imageUrl"
            [ngModel]="contact?.imageUrl"
            #imageUrl="ngModel"
          />
        </div>
      </div>

      <div class="row">
        <div class="form-group">
          <label for="groupList">Group Contacts:</label>
          <div class="alert alert-danger" *ngIf="invalidContact">
            Contact cannot be added to the group if it is already in the group
            or is the current contact!
          </div>
          <div
            class="row"
            id="groupList"
            style="min-height: 3rem"
            dnd-droppable
            [dropZones]="['contactGroup']"
            (onDropSuccess)="addToGroup($event)"
          >
            <div *ngFor="let contact of groupContacts; let i = index">
              <div>
                <cms-contact-item
                  class="col-sm-11"
                  [contact]="contact"
                ></cms-contact-item>
                <button
                  class="btn btn-danger col-sm-2 deleteButton"
                  (click)="onRemoveItem(i)"
                >
                  X
                </button>
              </div>
            </div>
            <span class="dragMsg" *ngIf="groupContacts?.length < 1"
              >Drag contacts in group here</span
            >
          </div>
        </div>
      </div>

      <div
        class="alert alert-danger"
        *ngIf="(!name.valid && name.touched) || (!email.valid && email.touched)"
      >
        The Name and Email fields are required.
      </div>

      <div class="row">
        <div class="col-xs-12 btn-toolbar">
          <button class="btn btn-success" type="submit" [disabled]="!f.valid">
            Save
          </button>
          <button class="btn btn-primary" type="button" (click)="onCancel()">
            Cancel
          </button>
        </div>
      </div>
    </form>
  </div>
</div>
