import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router, ActivatedRoute, Params } from '@angular/router';
import { Document } from '../document.model';
import { DocumentService } from '../document.service';

@Component({
  selector: 'cms-document-edit',
  templateUrl: './document-edit.component.html',
  styleUrls: ['./document-edit.component.css']
})
export class DocumentEditComponent implements OnInit {
  originalDocument!: Document | null;
  document: Document = new Document('', '', '', '', []);
  editMode: boolean = false;

  constructor(
    private documentService: DocumentService,
    private router: Router,
    private route: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.route.params.subscribe((params: Params) => {
      const id = params['id'];
      if (!id) {
        this.editMode = false;
        return;
      }
      this.originalDocument = this.documentService.getDocument(id);
      if (!this.originalDocument) {
        return;
      }
      this.editMode = true;
      this.document = JSON.parse(JSON.stringify(this.originalDocument));
    });
  }

  onCancel() {
    this.router.navigate(['/documents']);
  }

  onSubmit (form: NgForm): void {
    const values = form.value;
    // Assign form values to newDocument properties
    const newDocument = new Document(
      `${Math.floor(Math.random() * 1000) + 1}`, // random string 
      values.name, 
      values.description, 
      values.url, 
      []
    );    

    if (this.editMode && this.originalDocument) {
      this.documentService.updateDocument(this.originalDocument, newDocument);
    } else {
      this.documentService.addDocument(newDocument);
    }

    this.router.navigate(['/documents']);
  }
}
<div class="panel panel-default">
  <div class="panel-heading">
    <div class="row pad-left-right">
      <h3 class="pull-left title">{{ editMode ? 'Edit Document' : 'Add Document' }}</h3>
    </div>
  </div>
  <div class="panel-body">

    <form id="document-edit" #f="ngForm" (ngSubmit)="onSubmit(f)">
      <div class="row">
        <div class="col-sm-12 form-group">
          <label for="name">Document Title:</label>
          <input
            #name="ngModel"
            [(ngModel)]="document.name"
            class="form-control"
            id="name"
            max="120"
            name="name"
            required
            size="120"
            type="text"
          >
          <div
            *ngIf="name.invalid && (name.dirty || name.touched)"
            class="alert alert-danger"
            role="alert"
          >
            The Document Title is required.
          </div>
        </div>
      </div>

      <div class="row">
        <div class="col-sm-12 form-group">
          <label for="description">Document Description:</label>
          <input
            #description="ngModel"
            [(ngModel)]="document.description"
            class="form-control"
            id="description"
            max="255"
            name="description"
            type="text"
            size="120"
          >
          <div
            *ngIf="description.invalid && (description.dirty || description.touched)"
            class="alert alert-danger"
            role="alert"
          >
            The Document Title is required.
          </div>
        </div>
      </div>

      <div class="row">
        <div class="col-sm-12 form-group">
          <label for="url">Document URL:</label>
          <input
            #url="ngModel"
            [(ngModel)]="document.url"
            class="form-control"
            id="url"
            max="255"
            name="url"
            required
            size="150"
            type="text"
          >
          <div
            *ngIf="url.invalid && (url.dirty || url.touched)"
            class="alert alert-danger"
            role="alert"
          >
            The Document URL is required.
          </div>
        </div>
      </div>

      <div class="row">
        <div class="col-xs-12 btn-toolbar">
          <button [disabled]="f.invalid" class="btn btn-success" type="submit">Save</button>
          <button class="btn btn-primary" type="button" (click)="onCancel()">Cancel</button>
        </div>
      </div>

    </form>

  </div>
</div>
